import React, { useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FiSearch, FiPlus, FiMessageSquare, FiThumbsUp, FiEye, FiFilter } from 'react-icons/fi';

const BoardContainer = styled.div`
  width: 100%;
  min-height: 100vh;
  background: #f5f5f5;
`;

const Header = styled.div`
  background: linear-gradient(135deg, #00A86B 0%, #20B2AA 100%);
  padding: 60px 20px 20px;
  color: white;
`;

const HeaderContent = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;
`;

const Title = styled.h1`
  font-size: 24px;
  font-weight: 700;
`;

const WriteButton = styled.button`
  background: rgba(255, 255, 255, 0.2);
  border: none;
  border-radius: 12px;
  color: white;
  padding: 12px 16px;
  cursor: pointer;
  backdrop-filter: blur(10px);
  font-weight: 600;
  display: flex;
  align-items: center;
  gap: 8px;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(255, 255, 255, 0.3);
  }
`;

const SearchContainer = styled.div`
  position: relative;
  margin-bottom: 20px;
`;

const SearchInput = styled.input`
  width: 100%;
  padding: 12px 16px 12px 48px;
  border: none;
  border-radius: 12px;
  background: rgba(255, 255, 255, 0.15);
  color: white;
  backdrop-filter: blur(10px);
  font-size: 16px;

  &::placeholder {
    color: rgba(255, 255, 255, 0.7);
  }

  &:focus {
    outline: none;
    background: rgba(255, 255, 255, 0.25);
  }
`;

const SearchIcon = styled.div`
  position: absolute;
  left: 16px;
  top: 50%;
  transform: translateY(-50%);
  color: rgba(255, 255, 255, 0.7);
`;

const BoardTabs = styled.div`
  display: flex;
  background: rgba(255, 255, 255, 0.15);
  border-radius: 12px;
  padding: 4px;
  backdrop-filter: blur(10px);
  overflow-x: auto;
  gap: 4px;
`;

const BoardTab = styled.button`
  padding: 8px 16px;
  border: none;
  background: ${props => props.active ? 'white' : 'transparent'};
  color: ${props => props.active ? '#00A86B' : 'white'};
  border-radius: 8px;
  font-weight: 600;
  font-size: 14px;
  white-space: nowrap;
  transition: all 0.3s ease;
  cursor: pointer;
`;

const ContentArea = styled.div`
  padding: 20px;
`;

const FilterBar = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 16px;
`;

const FilterButton = styled.button`
  display: flex;
  align-items: center;
  gap: 8px;
  background: white;
  border: 1px solid #e9ecef;
  border-radius: 8px;
  padding: 8px 12px;
  color: #666;
  font-size: 14px;
  cursor: pointer;

  &:hover {
    border-color: #00A86B;
    color: #00A86B;
  }
`;

const PostCard = styled(motion.div)`
  background: white;
  border-radius: 16px;
  padding: 20px;
  margin-bottom: 12px;
  box-shadow: 0 2px 12px rgba(0, 0, 0, 0.08);
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.12);
  }
`;

const PostHeader = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 12px;
`;

const PostInfo = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;

  .anonymous {
    background: #f8f9fa;
    color: #666;
    padding: 4px 8px;
    border-radius: 6px;
    font-size: 12px;
    font-weight: 600;
  }

  .time {
    color: #999;
    font-size: 12px;
  }
`;

const PostBadge = styled.span`
  background: ${props => props.hot ? '#ff4757' : '#00A86B'};
  color: white;
  padding: 4px 8px;
  border-radius: 6px;
  font-size: 12px;
  font-weight: 600;
`;

const PostContent = styled.div`
  margin-bottom: 16px;

  h3 {
    font-size: 16px;
    font-weight: 600;
    color: #333;
    margin-bottom: 8px;
    line-height: 1.4;
  }

  p {
    color: #666;
    font-size: 14px;
    line-height: 1.5;
    display: -webkit-box;
    -webkit-line-clamp: 3;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }
`;

const PostStats = styled.div`
  display: flex;
  align-items: center;
  gap: 16px;
  padding-top: 12px;
  border-top: 1px solid #f0f0f0;

  .stat {
    display: flex;
    align-items: center;
    gap: 4px;
    color: #666;
    font-size: 12px;

    &.liked {
      color: #00A86B;
    }
  }
`;

const AnnouncementCard = styled(PostCard)`
  border: 2px solid #00A86B;
  background: linear-gradient(135deg, rgba(0, 168, 107, 0.05) 0%, rgba(32, 178, 170, 0.05) 100%);

  .announcement-badge {
    background: #00A86B;
    color: white;
    padding: 4px 8px;
    border-radius: 6px;
    font-size: 12px;
    font-weight: 600;
    margin-bottom: 8px;
    display: inline-block;
  }
`;

const BoardPage = ({ user }) => {
  const [activeBoard, setActiveBoard] = useState('자유게시판');
  const [searchQuery, setSearchQuery] = useState('');

  const boards = [
    '자유게시판',
    '정보게시판', 
    '취업게시판',
    '강의평가',
    '동아리',
    '학과게시판'
  ];

  const posts = [
    {
      id: 1,
      title: '청산학원대학교 학식 추천 메뉴',
      content: '신입생인데 학식이 너무 많아서 뭘 먹어야 할지 모르겠어요. 맛있는 메뉴 추천해주세요! 특히 가성비 좋은 메뉴가 있으면...',
      time: '2분 전',
      likes: 15,
      comments: 8,
      views: 124,
      isHot: true,
      board: '자유게시판'
    },
    {
      id: 2,
      title: '경영학부 필수과목 이수 순서 질문',
      content: '경영학부 2년인데 다음 학기 수강신청을 어떻게 해야 할지 고민입니다. 선배들 조언 부탁드려요.',
      time: '15분 전',
      likes: 23,
      comments: 12,
      views: 89,
      isHot: false,
      board: '정보게시판'
    },
    {
      id: 3,
      title: 'IT 대기업 인턴 합격 후기',
      content: '작년에 지원했던 IT 대기업 인턴에 합격해서 후기 공유합니다. 준비 과정이나 면접 팁 궁금한 거 있으면 댓글로...',
      time: '1시간 전',
      likes: 45,
      comments: 28,
      views: 312,
      isHot: true,
      board: '취업게시판'
    },
    {
      id: 4,
      title: '마케팅론 강의평가',
      content: '田中 교수님 마케팅론 수강했는데 정말 좋았어요. 실무 경험이 풍부하셔서 실제 사례를 많이 들려주십니다.',
      time: '3시간 전',
      likes: 18,
      comments: 6,
      views: 156,
      isHot: false,
      board: '강의평가'
    }
  ];

  const announcements = [
    {
      id: 1,
      title: '2025년 1학기 수강신청 일정 안내',
      content: '수강신청 기간: 2025년 2월 10일 ~ 2월 14일\n신입생 오리엔테이션: 2025년 3월 2일\n개강일: 2025년 3월 4일',
      time: '1일 전'
    }
  ];

  const filteredPosts = posts.filter(post => 
    (activeBoard === '전체' || post.board === activeBoard) &&
    (searchQuery === '' || post.title.toLowerCase().includes(searchQuery.toLowerCase()) || 
     post.content.toLowerCase().includes(searchQuery.toLowerCase()))
  );

  return (
    <BoardContainer>
      <Header>
        <HeaderContent>
          <Title>게시판</Title>
          <WriteButton>
            <FiPlus size={16} />
            글쓰기
          </WriteButton>
        </HeaderContent>

        <SearchContainer>
          <SearchIcon>
            <FiSearch size={20} />
          </SearchIcon>
          <SearchInput
            placeholder="게시글 검색..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </SearchContainer>

        <BoardTabs>
          {boards.map((board) => (
            <BoardTab
              key={board}
              active={activeBoard === board}
              onClick={() => setActiveBoard(board)}
            >
              {board}
            </BoardTab>
          ))}
        </BoardTabs>
      </Header>

      <ContentArea>
        <FilterBar>
          <FilterButton>
            <FiFilter size={16} />
            필터
          </FilterButton>
          <span style={{ fontSize: '14px', color: '#666' }}>
            총 {filteredPosts.length}개의 게시글
          </span>
        </FilterBar>

        {/* Announcements */}
        {announcements.map((announcement) => (
          <AnnouncementCard key={announcement.id}>
            <div className="announcement-badge">공지사항</div>
            <PostContent>
              <h3>{announcement.title}</h3>
              <p style={{ whiteSpace: 'pre-line' }}>{announcement.content}</p>
            </PostContent>
            <PostStats>
              <div className="stat">
                <span>📌</span>
                <span>중요</span>
              </div>
              <div className="stat">
                <span>{announcement.time}</span>
              </div>
            </PostStats>
          </AnnouncementCard>
        ))}

        {/* Regular Posts */}
        {filteredPosts.map((post) => (
          <PostCard
            key={post.id}
            whileHover={{ y: -2 }}
            whileTap={{ scale: 0.98 }}
          >
            <PostHeader>
              <PostInfo>
                <span className="anonymous">익명</span>
                <span className="time">{post.time}</span>
              </PostInfo>
              {post.isHot && <PostBadge hot>HOT</PostBadge>}
            </PostHeader>

            <PostContent>
              <h3>{post.title}</h3>
              <p>{post.content}</p>
            </PostContent>

            <PostStats>
              <div className="stat">
                <FiThumbsUp size={14} />
                <span>{post.likes}</span>
              </div>
              <div className="stat">
                <FiMessageSquare size={14} />
                <span>{post.comments}</span>
              </div>
              <div className="stat">
                <FiEye size={14} />
                <span>{post.views}</span>
              </div>
            </PostStats>
          </PostCard>
        ))}
      </ContentArea>
    </BoardContainer>
  );
};

export default BoardPage;
