import React, { useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FiMapPin, FiSearch, FiNavigation, FiCoffee, FiBook, FiShoppingBag, FiHome, FiBus } from 'react-icons/fi';

const MapContainer = styled.div`
  width: 100%;
  min-height: 100vh;
  background: #f5f5f5;
`;

const Header = styled.div`
  background: linear-gradient(135deg, #00A86B 0%, #20B2AA 100%);
  padding: 60px 20px 20px;
  color: white;
`;

const Title = styled.h1`
  font-size: 24px;
  font-weight: 700;
  margin-bottom: 20px;
`;

const SearchContainer = styled.div`
  position: relative;
  margin-bottom: 20px;
`;

const SearchInput = styled.input`
  width: 100%;
  padding: 12px 16px 12px 48px;
  border: none;
  border-radius: 12px;
  background: rgba(255, 255, 255, 0.15);
  color: white;
  backdrop-filter: blur(10px);
  font-size: 16px;

  &::placeholder {
    color: rgba(255, 255, 255, 0.7);
  }

  &:focus {
    outline: none;
    background: rgba(255, 255, 255, 0.25);
  }
`;

const SearchIcon = styled.div`
  position: absolute;
  left: 16px;
  top: 50%;
  transform: translateY(-50%);
  color: rgba(255, 255, 255, 0.7);
`;

const CategoryTabs = styled.div`
  display: flex;
  background: rgba(255, 255, 255, 0.15);
  border-radius: 12px;
  padding: 4px;
  backdrop-filter: blur(10px);
  overflow-x: auto;
  gap: 4px;
`;

const CategoryTab = styled.button`
  padding: 8px 16px;
  border: none;
  background: ${props => props.active ? 'white' : 'transparent'};
  color: ${props => props.active ? '#00A86B' : 'white'};
  border-radius: 8px;
  font-weight: 600;
  font-size: 14px;
  white-space: nowrap;
  transition: all 0.3s ease;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 6px;
`;

const ContentArea = styled.div`
  padding: 20px;
`;

const MapView = styled.div`
  background: white;
  border-radius: 16px;
  height: 300px;
  margin-bottom: 20px;
  box-shadow: 0 2px 12px rgba(0, 0, 0, 0.08);
  position: relative;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
  background-image: 
    radial-gradient(circle at 25% 25%, #00A86B 2px, transparent 2px),
    radial-gradient(circle at 75% 75%, #20B2AA 2px, transparent 2px),
    radial-gradient(circle at 50% 50%, #00A86B 1px, transparent 1px);
  background-size: 50px 50px, 60px 60px, 30px 30px;
  background-color: #f8f9fa;
`;

const MapPlaceholder = styled.div`
  text-align: center;
  color: #666;

  .icon {
    font-size: 48px;
    color: #00A86B;
    margin-bottom: 12px;
  }

  h3 {
    font-size: 18px;
    font-weight: 600;
    margin-bottom: 8px;
  }

  p {
    font-size: 14px;
    opacity: 0.8;
  }
`;

const LocationList = styled.div`
  display: grid;
  gap: 12px;
`;

const LocationCard = styled(motion.div)`
  background: white;
  border-radius: 16px;
  padding: 20px;
  box-shadow: 0 2px 12px rgba(0, 0, 0, 0.08);
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.12);
  }
`;

const LocationHeader = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 12px;
`;

const LocationInfo = styled.div`
  display: flex;
  align-items: center;
  gap: 12px;

  .icon {
    width: 40px;
    height: 40px;
    background: ${props => props.color || '#00A86B'};
    border-radius: 12px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
  }

  .details {
    h4 {
      font-size: 16px;
      font-weight: 600;
      color: #333;
      margin-bottom: 4px;
    }

    p {
      font-size: 12px;
      color: #666;
    }
  }
`;

const LocationBadge = styled.span`
  background: ${props => props.color || '#00A86B'};
  color: white;
  padding: 4px 8px;
  border-radius: 6px;
  font-size: 12px;
  font-weight: 600;
`;

const LocationDescription = styled.div`
  margin-bottom: 12px;

  p {
    color: #666;
    font-size: 14px;
    line-height: 1.5;
  }
`;

const LocationTags = styled.div`
  display: flex;
  gap: 8px;
  flex-wrap: wrap;
  margin-bottom: 12px;
`;

const Tag = styled.span`
  background: #f8f9fa;
  color: #666;
  padding: 4px 8px;
  border-radius: 6px;
  font-size: 12px;
  font-weight: 500;
`;

const LocationActions = styled.div`
  display: flex;
  gap: 8px;
  padding-top: 12px;
  border-top: 1px solid #f0f0f0;
`;

const ActionButton = styled.button`
  flex: 1;
  padding: 8px 12px;
  background: ${props => props.primary ? '#00A86B' : '#f8f9fa'};
  color: ${props => props.primary ? 'white' : '#666'};
  border: none;
  border-radius: 8px;
  font-size: 12px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: ${props => props.primary ? '#008a5a' : '#e9ecef'};
  }
`;

const MapPage = ({ user }) => {
  const [activeCategory, setActiveCategory] = useState('전체');
  const [searchQuery, setSearchQuery] = useState('');

  const categories = [
    { id: '전체', label: '전체', icon: FiMapPin },
    { id: '학습', label: '학습', icon: FiBook },
    { id: '식당', label: '식당', icon: FiCoffee },
    { id: '편의시설', label: '편의시설', icon: FiShoppingBag },
    { id: '기숙사', label: '기숙사', icon: FiHome },
    { id: '교통', label: '교통', icon: FiBus }
  ];

  const locations = [
    {
      id: 1,
      name: '17호관 (경영학부)',
      category: '학습',
      description: '경영학부 전용 건물. 강의실, 세미나실, 학생 라운지 완비',
      tags: ['Wi-Fi', '프린터', '카페', '조용한'],
      distance: '도보 3분',
      rating: 4.5,
      color: '#00A86B'
    },
    {
      id: 2,
      name: '청山 학생식당',
      category: '식당',
      description: '다양한 한식, 일식, 양식 메뉴. 학생 할인 적용',
      tags: ['저렴', '맛있는', '넓은', '학생할인'],
      distance: '도보 5분',
      rating: 4.2,
      color: '#ff6b6b'
    },
    {
      id: 3,
      name: '중앙도서관',
      category: '학습',
      description: '24시간 열람실, 그룹 스터디룸, 디지털 자료실',
      tags: ['24시간', '조용한', 'Wi-Fi', '스터디룸'],
      distance: '도보 7분',
      rating: 4.7,
      color: '#4ecdc4'
    },
    {
      id: 4,
      name: '편의점 (세븐일레븐)',
      category: '편의시설',
      description: '캠퍼스 내 편의점. 생필품, 간식, 음료 판매',
      tags: ['24시간', 'ATM', '택배', '저렴'],
      distance: '도보 2분',
      rating: 4.0,
      color: '#45b7d1'
    },
    {
      id: 5,
      name: '청산 기숙사',
      category: '기숙사',
      description: '신축 기숙사. 1인실, 2인실 선택 가능',
      tags: ['깨끗한', '보안', 'Wi-Fi', '세탁실'],
      distance: '도보 10분',
      rating: 4.3,
      color: '#96ceb4'
    },
    {
      id: 6,
      name: '정문 버스정류장',
      category: '교통',
      description: '시내버스, 마을버스, 셔틀버스 정류장',
      tags: ['교통편리', '실시간정보', '지하철연결'],
      distance: '도보 1분',
      rating: 4.1,
      color: '#feca57'
    }
  ];

  const filteredLocations = locations.filter(location => 
    (activeCategory === '전체' || location.category === activeCategory) &&
    (searchQuery === '' || location.name.toLowerCase().includes(searchQuery.toLowerCase()) || 
     location.description.toLowerCase().includes(searchQuery.toLowerCase()))
  );

  return (
    <MapContainer>
      <Header>
        <Title>캠퍼스 맵</Title>

        <SearchContainer>
          <SearchIcon>
            <FiSearch size={20} />
          </SearchIcon>
          <SearchInput
            placeholder="장소 검색..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </SearchContainer>

        <CategoryTabs>
          {categories.map((category) => {
            const IconComponent = category.icon;
            return (
              <CategoryTab
                key={category.id}
                active={activeCategory === category.id}
                onClick={() => setActiveCategory(category.id)}
              >
                <IconComponent size={16} />
                {category.label}
              </CategoryTab>
            );
          })}
        </CategoryTabs>
      </Header>

      <ContentArea>
        <MapView>
          <MapPlaceholder>
            <div className="icon">
              <FiMapPin />
            </div>
            <h3>청산학원대학교 캠퍼스</h3>
            <p>인터랙티브 맵 (개발 예정)</p>
          </MapPlaceholder>
        </MapView>

        <LocationList>
          {filteredLocations.map((location) => (
            <LocationCard
              key={location.id}
              whileHover={{ y: -2 }}
              whileTap={{ scale: 0.98 }}
            >
              <LocationHeader>
                <LocationInfo color={location.color}>
                  <div className="icon">
                    <FiMapPin size={20} />
                  </div>
                  <div className="details">
                    <h4>{location.name}</h4>
                    <p>{location.distance} • ⭐ {location.rating}</p>
                  </div>
                </LocationInfo>
                <LocationBadge color={location.color}>
                  {location.category}
                </LocationBadge>
              </LocationHeader>

              <LocationDescription>
                <p>{location.description}</p>
              </LocationDescription>

              <LocationTags>
                {location.tags.map((tag, index) => (
                  <Tag key={index}>#{tag}</Tag>
                ))}
              </LocationTags>

              <LocationActions>
                <ActionButton primary>
                  <FiNavigation size={12} style={{ marginRight: '4px' }} />
                  길찾기
                </ActionButton>
                <ActionButton>
                  <FiMapPin size={12} style={{ marginRight: '4px' }} />
                  상세정보
                </ActionButton>
              </LocationActions>
            </LocationCard>
          ))}
        </LocationList>
      </ContentArea>
    </MapContainer>
  );
};

export default MapPage;
