import React, { useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FiUser, FiTrendingUp, FiStar, FiBriefcase, FiCalendar, FiBell, FiSettings, FiLogOut, FiBus, FiBook, FiHome as FiHomeIcon } from 'react-icons/fi';

const HomeContainer = styled.div`
  width: 100%;
  min-height: 100vh;
  background: #f5f5f5;
`;

const Header = styled.div`
  background: linear-gradient(135deg, #00A86B 0%, #20B2AA 100%);
  padding: 60px 20px 30px;
  color: white;
  position: relative;
`;

const UserInfo = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 20px;
`;

const UserProfile = styled.div`
  display: flex;
  align-items: center;
  gap: 12px;

  .avatar {
    width: 50px;
    height: 50px;
    background: rgba(255, 255, 255, 0.2);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    backdrop-filter: blur(10px);
  }

  .info {
    h3 {
      font-size: 18px;
      font-weight: 600;
      margin-bottom: 4px;
    }

    p {
